INSERT INTO `news` (`id`, `OrderBy`, `title`, `date`, `MainBody`, `poster`) VALUES
(3, 0, 'We are looking for new members', '2013-02-17', '<p>No experience necessary as full tuition will be given.<br />\r\n<br />\r\nPractice is every Thursday at 7pm in the Orange Hall, Canal Place, Saltcoats. <br />\r\n<br />\r\nPop along and meet the band - you will be made more than welcome.</p>', 3),
(20, 0, 'SPB Goods For Sale', '2012-06-29', '<p>The following goods are available for purchase:</p>\r\n\r\n<p><strong>SPB Polo shirts: &pound;15<br />\r\nSPB Jumpers: &pound;15<br />\r\nSPB Hoodies: &pound;20</strong></p>\r\n\r\n<p>The above come&nbsp;in various styles, colours and sizes. Please contact us for further information - <a href=\"mailto:treasurer@spb-fb.co.uk\">treasurer@spb-fb.co.uk</a></p>\r\n\r\n<p><u><strong>SPB CD</strong></u>&nbsp;</p>\r\n\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" width=\"100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align: center\"><img align=\"absBottom\" alt=\"\" height=\"75\" src=\"/userfiles/image/rsz_finished-cd-with-cover.png\" width=\"86\" /></td>\r\n			<td>\r\n			<p>Onwards and Upwards, our first CD is now available and priced at a very reasonable &pound;10.</p>\r\n\r\n			<p>Contact a member of committee for details on how to purchase.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;<u><strong>SPB Mugs</strong></u></p>\r\n\r\n<p>Priced at a very reasonable &pound;3 - limited stocks, so get in early if you&#39;re wanting one.</p>\r\n\r\n<div style=\"text-align: left\"><strong><u>Badges</u></strong><br />\r\n&nbsp;</div>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"/userfiles/image/badge3.jpg\" target=\"_blank\"><img align=\"right\" alt=\"SPBFB Gold Badge\" border=\"0\" height=\"58\" src=\"/userfiles/image/rsz_badge3.jpg\" width=\"86\" /></a></td>\r\n			<td>\r\n			<div style=\"text-align: left\">Wee SPB badges are gold trimmed and are approximately the size of a &pound;1 coin.</div>\r\n\r\n			<div style=\"text-align: justify\"><br />\r\n			They are priced at &pound;2 per badge, however, there will be a price reduction if buying in bulk - please contact us for further information -&nbsp; <a href=\"mailto:treasurer@spb-fb.co.uk\">treasurer@spb-fb.co.uk</a></div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<div style=\"text-align: left\">&nbsp;<br />\r\n<strong><u>Keyrings</u></strong><br />\r\n&nbsp;\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href=\"/userfiles/image/keyring.jpg\" target=\"_blank\"><img alt=\"SPBFB Keyring\" border=\"0\" height=\"91\" src=\"/userfiles/image/rsz_keyring.jpg\" width=\"73\" /></a></td>\r\n			<td>\r\n			<div style=\"text-align: justify\">The keyrings are priced at only&nbsp;<strong>&pound;1</strong> each.&nbsp;</div>\r\n\r\n			<div style=\"text-align: left\">Similar to the badges, if buying in bulk please contact us to discuss a price.</div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n', 3),
(76, 0, 'Our New Crest', '2012-04-03', '<p><img alt=\"\" src=\"userfiles/image/spbpd4.png\" style=\"width: 171px; height: 159px; border-width: 0px; border-style: solid; float: left;\" />Saltcoats Protestant Boys FB have been working hard over the winter period to produce a more modern version of our bands crest as we felt after producing a CD (which is still for sale from any band member) we needed to modernize and further raise our standards.</p>\r\n\r\n<p><img alt=\"\" src=\"userfiles/image/final%20image%20with%20wording.png\" style=\"width: 170px; height: 200px; float: right;\" />We first approached Band Parades who created a lovely crest for us based on a design recently painted onto one of our bass drums.<br />\r\nThough we really liked this design we thought it better to go one step more and really give it that &#39;WOW&#39; factor.</p>\r\n\r\n<p>After some research we contacted reputable Graphics Designer, Tracey Armstrong, who patiently enhanced the design for us. Her work from the word go blew us away and we knew we were going to be very happy with the outcome. She took the design which Band Parades had done for us only leaving the Flags intact she refined the rest of the badge and even our linked SPB logo to make it more modern looking.</p>\r\n\r\n<p>Saltcoats Protestant Boys thank both Band Parades and Tracey for the fantastic work they have done for us.</p>\r\n\r\n<p style=\"text-align: center;\">Onwards and Upwards!<br><img alt=\"\" src=\"userfiles/image/new%20badge.png\" style=\"width: 331px; height: 319px;\" /></p>', 3);
